package gatewayapi

import (
	"net"

	"github.com/gruntwork-io/terratest/modules/k8s"
	. "github.com/onsi/ginkgo/v2"
	. "github.com/onsi/gomega"

	config_core "github.com/kumahq/kuma/pkg/config/core"
	. "github.com/kumahq/kuma/test/framework"
	client "github.com/kumahq/kuma/test/framework/client"
	"github.com/kumahq/kuma/test/framework/deployments/testserver"
)

func GatewayAPICRDs(cluster Cluster) error {
	out, err := k8s.RunKubectlAndGetOutputE(
		cluster.GetTesting(),
		cluster.GetKubectlOptions(),
		"kustomize", "github.com/kubernetes-sigs/gateway-api/config/crd?ref=v0.4.0",
	)
	if err != nil {
		return err
	}

	return k8s.KubectlApplyFromStringE(cluster.GetTesting(), cluster.GetKubectlOptions(), out)
}

const GatewayClass = `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: GatewayClass
metadata:
  name: kuma
spec:
  controllerName: gateways.kuma.io/controller
`

var cluster *K8sCluster

var _ = E2EBeforeSuite(func() {
	if Config.IPV6 {
		return // KIND which is used for IPV6 tests does not support load balancer that is used in this test.
	}

	cluster = NewK8sCluster(NewTestingT(), Kuma1, Silent)

	externalService := `
apiVersion: kuma.io/v1alpha1
kind: ExternalService
mesh: default
metadata:
  name: external-service
spec:
  tags:
    kuma.io/service: external-service
    kuma.io/protocol: http
  networking:
    address: external-service.external-services.svc.cluster.local:80
`

	ExternalServicesNamespace := "external-services"
	err := NewClusterSetup().
		Install(GatewayAPICRDs).
		Install(Kuma(config_core.Standalone,
			WithCtlOpts(map[string]string{"--experimental-meshgateway": "true"}),
			WithCtlOpts(map[string]string{"--experimental-gatewayapi": "true"}),
		)).
		Install(NamespaceWithSidecarInjection(TestNamespace)).
		Install(testserver.Install(
			testserver.WithName("test-server-1"),
			testserver.WithNamespace(TestNamespace),
			testserver.WithArgs("echo", "--instance", "test-server-1"),
		)).
		Install(testserver.Install(
			testserver.WithName("test-server-2"),
			testserver.WithNamespace(TestNamespace),
			testserver.WithArgs("echo", "--instance", "test-server-2"),
		)).
		Install(Namespace(ExternalServicesNamespace)).
		Install(testserver.Install(
			testserver.WithName("external-service"),
			testserver.WithNamespace(ExternalServicesNamespace),
			testserver.WithArgs("echo", "--instance", "external-service"),
		)).
		Install(YamlK8s(externalService)).
		Install(YamlK8s(GatewayClass)).
		Setup(cluster)
	Expect(err).ToNot(HaveOccurred())

	E2EDeferCleanup(func() {
		Expect(cluster.DeleteNamespace(TestNamespace)).To(Succeed())
		Expect(cluster.DeleteNamespace(ExternalServicesNamespace)).To(Succeed())
		Expect(cluster.DeleteKuma()).To(Succeed())
		Expect(cluster.DismissCluster()).To(Succeed())
	})
})

func GatewayAPI() {
	if Config.IPV6 {
		return // KIND which is used for IPV6 tests does not support load balancer that is used in this test.
	}

	GatewayIP := func() string {
		var ip string
		Eventually(func(g Gomega) {
			out, err := k8s.RunKubectlAndGetOutputE(
				cluster.GetTesting(),
				cluster.GetKubectlOptions(TestNamespace),
				"get", "gateway", "kuma", "-ojsonpath={.status.addresses[0].value}",
			)
			g.Expect(err).ToNot(HaveOccurred())
			g.Expect(out).ToNot(BeEmpty())
			ip = out
		}, "60s", "1s").Should(Succeed(), "could not get a LoadBalancer IP of the Gateway")
		return ip
	}

	Describe("GatewayClass parametersRef", Ordered, func() {
		haGatewayClass := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: GatewayClass
metadata:
  name: kuma
spec:
  controllerName: gateways.kuma.io/controller
  parametersRef:
    kind: MeshGatewayConfig
    group: kuma.io
    name: ha-config`

		haConfig := `
apiVersion: kuma.io/v1alpha1
kind: MeshGatewayConfig
metadata:
  name: ha-config
spec:
  replicas: 3`

		haGateway := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: Gateway
metadata:
  name: kuma
  namespace: kuma-test
spec:
  gatewayClassName: kuma
  listeners:
  - name: proxy
    port: 8080
    protocol: HTTP`

		BeforeAll(func() {
			E2EDeferCleanup(func() error {
				return k8s.RunKubectlE(cluster.GetTesting(), cluster.GetKubectlOptions(TestNamespace), "delete", "gateway", "kuma")
			})
			Expect(YamlK8s(haConfig)(cluster)).To(Succeed())
			Expect(YamlK8s(haGatewayClass)(cluster)).To(Succeed())
			Expect(YamlK8s(haGateway)(cluster)).To(Succeed())
		})

		It("should create the right number of pods", func() {
			Expect(cluster.WaitApp("kuma", "kuma-test", 3)).To(Succeed())

			newHaConfig := `
apiVersion: kuma.io/v1alpha1
kind: MeshGatewayConfig
metadata:
  name: ha-config
spec:
  replicas: 4`
			Expect(YamlK8s(newHaConfig)(cluster)).To(Succeed())

			Expect(cluster.WaitApp("kuma", "kuma-test", 4)).To(Succeed())
		})
	})

	Context("HTTP Gateway", Ordered, func() {
		gateway := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: Gateway
metadata:
  name: kuma
  namespace: kuma-test
spec:
  gatewayClassName: kuma
  listeners:
  - name: proxy
    port: 8080
    protocol: HTTP`

		var address string

		BeforeAll(func() {
			E2EDeferCleanup(func() error {
				return k8s.RunKubectlE(cluster.GetTesting(), cluster.GetKubectlOptions(TestNamespace), "delete", "gateway", "kuma")
			})
			Expect(YamlK8s(gateway)(cluster)).To(Succeed())
			address = net.JoinHostPort(GatewayIP(), "8080")
		})

		It("should route the traffic to test-server by path", func() {
			// given
			route := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: HTTPRoute
metadata:
  name: test-server-paths
  namespace: kuma-test
spec:
  parentRefs:
  - name: kuma
  rules:
  - backendRefs:
    - name: test-server-1
      port: 80
    matches:
    - path:
        type: PathPrefix
        value: /1
  - backendRefs:
    - name: test-server-2
      port: 80
    matches:
    - path:
        type: PathPrefix
        value: /2`

			// when
			err := YamlK8s(route)(cluster)

			// then
			Expect(err).ToNot(HaveOccurred())

			Eventually(func(g Gomega) {
				resp, err := client.CollectResponseDirectly("http://" + address + "/1")
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(resp.Instance).To(Equal("test-server-1"))
			}, "30s", "1s").Should(Succeed())

			Eventually(func(g Gomega) {
				resp, err := client.CollectResponseDirectly("http://" + address + "/2")
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(resp.Instance).To(Equal("test-server-2"))
			}, "30s", "1s").Should(Succeed())
		})

		It("should route the traffic to test-server by header", func() {
			// given
			routes := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: HTTPRoute
metadata:
  name: test-server-1
  namespace: kuma-test
spec:
  parentRefs:
  - name: kuma
  hostnames:
  - "test-server-1.com"
  rules:
  - backendRefs:
    - name: test-server-1
      port: 80
---
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: HTTPRoute
metadata:
  name: test-server-2
  namespace: kuma-test
spec:
  parentRefs:
  - name: kuma
  hostnames:
  - "test-server-2.com"
  rules:
  - backendRefs:
    - name: test-server-2
      port: 80
`

			// when
			err := YamlK8s(routes)(cluster)

			// then
			Expect(err).ToNot(HaveOccurred())

			Eventually(func(g Gomega) {
				resp, err := client.CollectResponseDirectly("http://"+address, client.WithHeader("host", "test-server-1.com"))
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(resp.Instance).To(Equal("test-server-1"))
			}, "30s", "1s").Should(Succeed())

			Eventually(func(g Gomega) {
				resp, err := client.CollectResponseDirectly("http://"+address, client.WithHeader("host", "test-server-2.com"))
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(resp.Instance).To(Equal("test-server-2"))
			}, "30s", "1s").Should(Succeed())
		})

		It("should route to external service", func() {
			// given
			routes := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: HTTPRoute
metadata:
  name: external-service
  namespace: kuma-test
spec:
  parentRefs:
  - name: kuma
  hostnames:
  - "external-service.com"
  rules:
  - backendRefs:
    - group: kuma.io
      kind: ExternalService
      name: external-service
`

			// when
			err := YamlK8s(routes)(cluster)

			// then
			Expect(err).ToNot(HaveOccurred())

			Eventually(func(g Gomega) {
				resp, err := client.CollectResponseDirectly("http://"+address, client.WithHeader("host", "external-service.com"))
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(resp.Instance).To(Equal("external-service"))
			}, "30s", "1s").Should(Succeed())
		})
	})

	Context("HTTPS Gateway", Ordered, func() {
		secret := `
apiVersion: v1
kind: Secret
metadata:
  name: secret-tls
  namespace: kuma-test
type: kubernetes.io/tls
data:
  tls.crt: ""
  tls.key: ""
`
		gateway := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: Gateway
metadata:
  name: kuma
  namespace: kuma-test
spec:
  gatewayClassName: kuma
  listeners:
  - name: proxy
    port: 8090
    hostname: 'test-server-1.com'
    protocol: HTTPS
    tls:
      certificateRefs:
      - name: secret-tls
  - name: proxy-wildcard
    port: 8091
    protocol: HTTPS
    tls:
      certificateRefs:
      - name: secret-tls`

		var ip string

		BeforeAll(func() {
			E2EDeferCleanup(func() error {
				return k8s.RunKubectlE(cluster.GetTesting(), cluster.GetKubectlOptions(TestNamespace), "delete", "gateway", "kuma")
			})
			Expect(YamlK8s(secret)(cluster)).To(Succeed())
			Expect(YamlK8s(gateway)(cluster)).To(Succeed())
			ip = GatewayIP()
		})

		It("should route the traffic using TLS", func() {
			// given
			route := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: HTTPRoute
metadata:
  name: test-server-paths
  namespace: kuma-test
spec:
  parentRefs:
  - name: kuma
  rules:
  - backendRefs:
    - name: test-server-1
      port: 80
    matches:
    - path:
        type: PathPrefix
        value: /`

			// when
			err := YamlK8s(route)(cluster)

			// then
			Expect(err).ToNot(HaveOccurred())

			Eventually(func(g Gomega) {
				resp, err := client.CollectResponseDirectly("https://"+net.JoinHostPort(ip, "8090"), client.WithHeader("host", "test-server-1.com"))
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(resp.Instance).To(Equal("test-server-1"))
			}, "30s", "1s").Should(Succeed())

			Eventually(func(g Gomega) {
				resp, err := client.CollectResponseDirectly("https://" + net.JoinHostPort(ip, "8091"))
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(resp.Instance).To(Equal("test-server-1"))
			}, "30s", "1s").Should(Succeed())
		})

		It("should manage Kuma Secret", func() {
			// given converted Kuma secret
			const convertedSecretName = "gapi-kuma-test-secret-tls"
			var kumaSecret string
			Eventually(func(g Gomega) {
				out, err := cluster.GetKumactlOptions().RunKumactlAndGetOutput("get", "secret", convertedSecretName, "-o", "json")
				g.Expect(err).ToNot(HaveOccurred())
				kumaSecret = out
			}, "30s", "1s").Should(Succeed())

			// when original secret is changed
			secret = `
apiVersion: v1
kind: Secret
metadata:
  name: secret-tls
  namespace: kuma-test
type: kubernetes.io/tls
data:
  tls.crt: ""
  tls.key: ""
`
			err := YamlK8s(secret)(cluster)

			// then copied secret is also changed
			Expect(err).ToNot(HaveOccurred())
			Eventually(func(g Gomega) {
				out, err := cluster.GetKumactlOptions().RunKumactlAndGetOutput("get", "secret", convertedSecretName, "-o", "json")
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(out).ToNot(MatchJSON(kumaSecret))
			}, "30s", "1s").Should(Succeed())

			// when original secret is removed
			err = k8s.RunKubectlE(cluster.GetTesting(), cluster.GetKubectlOptions(TestNamespace), "delete", "secret", "secret-tls")

			// then copied secret is removed
			Expect(err).ToNot(HaveOccurred())
			Eventually(func(g Gomega) {
				out, err := cluster.GetKumactlOptions().RunKumactlAndGetOutput("get", "secrets")
				g.Expect(err).ToNot(HaveOccurred())
				g.Expect(out).ToNot(ContainSubstring(convertedSecretName))
			}, "30s", "1s").Should(Succeed())
		})
	})

	Context("Upstream validation", func() {
		It("should validate Gateway", func() {
			gateway := `
apiVersion: gateway.networking.k8s.io/v1alpha2
kind: Gateway
metadata:
  name: kuma
  namespace: kuma-test
spec:
  gatewayClassName: kuma
  listeners:
  - name: proxy
    port: 8080
    protocol: TCP
    hostname: xyz.io`
			err := k8s.KubectlApplyFromStringE(cluster.GetTesting(), cluster.GetKubectlOptions(), gateway)
			Expect(err).To(HaveOccurred())
		})
	})
}
