package externalservices

import (
	"encoding/base64"
	"fmt"
	"net"

	. "github.com/onsi/ginkgo/v2"
	. "github.com/onsi/gomega"
	"github.com/onsi/gomega/types"
	"google.golang.org/protobuf/types/known/wrapperspb"

	mesh_proto "github.com/kumahq/kuma/api/mesh/v1alpha1"
	system_proto "github.com/kumahq/kuma/api/system/v1alpha1"
	core_mesh "github.com/kumahq/kuma/pkg/core/resources/apis/mesh"
	test_model "github.com/kumahq/kuma/pkg/test/resources/model"
	"github.com/kumahq/kuma/test/e2e_env/universal/env"
	. "github.com/kumahq/kuma/test/framework"
)

func Policy() {
	meshName := "external-service-base"
	meshDefaulMtlsOn := fmt.Sprintf(`
type: Mesh
name: "%s"
mtls:
  enabledBackend: ca-1
  backends:
  - name: ca-1
    type: builtin
networking:
  outbound:
    passthrough: false
`, meshName)

	externalService := func(service, address string, tls bool, caCert []byte) *core_mesh.ExternalServiceResource {
		res := &core_mesh.ExternalServiceResource{
			Meta: &test_model.ResourceMeta{
				Mesh: meshName,
				Name: service,
			},
			Spec: &mesh_proto.ExternalService{
				Tags: map[string]string{
					mesh_proto.ServiceTag:  service,
					mesh_proto.ProtocolTag: core_mesh.ProtocolHTTP,
				},
				Networking: &mesh_proto.ExternalService_Networking{
					Address: address,
					Tls: &mesh_proto.ExternalService_Networking_TLS{
						Enabled: tls,
					},
				},
			},
		}
		if tls {
			res.Spec.Networking.Tls.CaCert = &system_proto.DataSource{
				Type: &system_proto.DataSource_Inline{
					Inline: &wrapperspb.BytesValue{
						Value: caCert,
					},
				},
			}
		}
		return res
	}

	var esHttpHostPort string
	var esHttp2HostPort string
	var esHttpsHostPort string

	BeforeAll(func() {
		err := NewClusterSetup().
			Install(YamlUniversal(meshDefaulMtlsOn)).
			Install(TestServerExternalServiceUniversal("es-http", meshName, 80, false)).
			Install(TestServerExternalServiceUniversal("es-https", meshName, 443, true)).
			Install(TestServerExternalServiceUniversal("es-http-2", meshName, 81, false)).
			Install(DemoClientUniversal("demo-client", meshName, WithTransparentProxy(true))).
			Setup(env.Cluster)
		Expect(err).ToNot(HaveOccurred())
		esHttpHostPort = net.JoinHostPort(env.Cluster.GetApp("es-http").GetContainerName(), "80")
		esHttp2HostPort = net.JoinHostPort(env.Cluster.GetApp("es-http-2").GetContainerName(), "81")
		esHttpsHostPort = net.JoinHostPort(env.Cluster.GetApp("es-https").GetContainerName(), "443")
	})

	E2EAfterAll(func() {
		Expect(env.Cluster.DeleteMeshApps(meshName)).To(Succeed())
		Expect(env.Cluster.DeleteMesh(meshName)).To(Succeed())
	})

	checkSuccessfulRequest := func(url string, matcher types.GomegaMatcher) {
		Eventually(func(g Gomega) {
			stdout, _, err := env.Cluster.Exec("", "", "demo-client",
				"curl", "-v", "-m", "3", "--fail", url)
			g.Expect(err).ToNot(HaveOccurred())
			g.Expect(stdout).To(ContainSubstring("HTTP/1.1 200 OK"))
			g.Expect(stdout).To(matcher)
		}).WithOffset(1).Should(Succeed())
	}

	It("should route to external-service", func() {
		ResourceUniversal(externalService("ext-srv-1", esHttpHostPort, false, nil))
		err := env.Cluster.Install(ResourceUniversal(externalService("ext-srv-1", esHttpHostPort, false, nil)))
		Expect(err).ToNot(HaveOccurred())

		checkSuccessfulRequest("ext-srv-1.mesh", And(
			Not(ContainSubstring("HTTPS")),
			// Should rewrite host header
			ContainSubstring(`"Host":["es-http.external-service-base"]`),
		))
		checkSuccessfulRequest(esHttpHostPort, Not(ContainSubstring("HTTPS")))
	})

	It("should route to external-service with same hostname but different ports", func() {
		err := env.Cluster.Install(ResourceUniversal(externalService("ext-srv-1", esHttpHostPort, false, nil)))
		Expect(err).ToNot(HaveOccurred())

		err = env.Cluster.Install(ResourceUniversal(externalService("ext-srv-2", esHttp2HostPort, false, nil)))
		Expect(err).ToNot(HaveOccurred())

		// when access the first external service with .mesh
		checkSuccessfulRequest("ext-srv-1.mesh",
			And(Not(ContainSubstring("HTTPS")), ContainSubstring("es-http")))
		checkSuccessfulRequest(esHttpHostPort,
			And(Not(ContainSubstring("HTTPS")), ContainSubstring("es-http")))

		checkSuccessfulRequest("ext-srv-2.mesh",
			And(Not(ContainSubstring("HTTPS")), ContainSubstring("es-http-2")))
		checkSuccessfulRequest(esHttp2HostPort,
			And(Not(ContainSubstring("HTTPS")), ContainSubstring("es-http-2")))
	})

	It("should route to external-service over tls", func() {
		// when set invalid certificate
		otherCert := ""
		caCert, _ := base64.StdEncoding.DecodeString(otherCert)
		err := env.Cluster.Install(ResourceUniversal(externalService("ext-srv-tls", esHttpsHostPort, true, caCert)))
		Expect(err).ToNot(HaveOccurred())

		// then accessing the secured external service fails
		Consistently(func() error {
			_, _, err = env.Cluster.Exec("", "", "demo-client",
				"curl", "-v", "-m", "3", "--fail", "http://"+esHttpsHostPort)
			return err
		}).Should(HaveOccurred())

		// when set proper certificate
		cert, _, err := env.Cluster.Exec("", "", "es-https", "cat /certs/cert.pem")
		Expect(err).ToNot(HaveOccurred())
		Expect(cert).ToNot(BeEmpty())

		err = env.Cluster.Install(ResourceUniversal(externalService("ext-srv-tls", esHttpsHostPort, true, []byte(cert))))
		Expect(err).ToNot(HaveOccurred())

		// then accessing the secured external service succeeds
		checkSuccessfulRequest("http://"+esHttpsHostPort, Not(ContainSubstring("HTTPS")))
	})

	// certauth.idrix.fr is a site for testing mTLS authentication
	// This site requires renegotiation because the server asks for the client certs as a second step
	// We want to run this only on demand because we've got bad experience tying up E2E to external service available on the internet
	// It's hard to rebuild this as a local service in the cluster because many servers dropped support for renegotiation.
	PIt("should check allow negotiation", func() {
		// given
		es := fmt.Sprintf(`
type: ExternalService
mesh: "%s"
name: testmtls
tags:
  kuma.io/service: testmtls
  kuma.io/protocol: http
networking:
  address: certauth.idrix.fr:443
  tls:
    enabled: true
    allowRenegotiation: true
    caCert:
      inline: ""
    clientCert: # we can pass any client tls pair to this server so here are certs generated by kumactl generate tls-certificate --type=client --key-file=client.key --cert-file=client.pem
      inline: ""
    clientKey:
      inline: ""
`, meshName)

		Expect(env.Cluster.Install(YamlUniversal(es))).To(Succeed())

		// then
		Eventually(func(g Gomega) {
			stdout, _, err := env.Cluster.Exec("", "", "demo-client",
				"curl", "-v", "--fail", "-m", "3", "testmtls.mesh")
			g.Expect(err).ToNot(HaveOccurred())
			g.Expect(stdout).To(ContainSubstring("HTTP/1.1 200 OK"))
			g.Expect(stdout).To(ContainSubstring("TLSv1.2 Authentication OK!"))
			g.Expect(stdout).To(ContainSubstring("CN=kuma"))
		}).Should(Succeed())
	})
}
